mapper.sql:
where state = true

   and id = #{id,jdbcType=VARCHAR}


   and title = #{title,jdbcType=VARCHAR}


   and image = #{image,jdbcType=VARCHAR}


   and topic_count = #{topicCount,jdbcType=INTEGER}


   and user_count = #{userCount,jdbcType=INTEGER}


   and create_time = #{createTime,jdbcType=TIMESTAMP}


   and update_time = #{updateTime,jdbcType=TIMESTAMP}


   and state = #{state,jdbcType=BIT}


   and integral = #{integral,jdbcType=INTEGER}

mapper.insert:
insert into t_questionnaire (id, title, image, 
  topic_count, user_count, create_time, 
  update_time, state, integral
  )
values (#{id,jdbcType=VARCHAR}, #{title,jdbcType=VARCHAR}, #{image,jdbcType=VARCHAR}, 
  #{topicCount,jdbcType=INTEGER}, #{userCount,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{state,jdbcType=BIT}, #{integral,jdbcType=INTEGER}
  )
