mapper.sql:
where state = true

   and id = #{id,jdbcType=VARCHAR}


   and product_id = #{productId,jdbcType=VARCHAR}


   and name = #{name,jdbcType=VARCHAR}


   and jackpot_count = #{jackpotCount,jdbcType=INTEGER}


   and create_user = #{createUser,jdbcType=VARCHAR}


   and create_time = #{createTime,jdbcType=TIMESTAMP}


   and update_user = #{updateUser,jdbcType=VARCHAR}


   and update_time = #{updateTime,jdbcType=TIMESTAMP}


   and state = #{state,jdbcType=BIT}

mapper.insert:
insert into t_jackpot_type (id, product_id, name, 
  jackpot_count, create_user, create_time, 
  update_user, update_time, state
  )
values (#{id,jdbcType=VARCHAR}, #{productId,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{jackpotCount,jdbcType=INTEGER}, #{createUser,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{updateUser,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}, #{state,jdbcType=BIT}
  )
