  create_time, update_time, state,max_score
  )
values (#{id,jdbcType=VARCHAR}, #{shareSn,jdbcType=VARCHAR}, #{wxnickname,jdbcType=VARCHAR}, 
  #{headImage,jdbcType=VARCHAR}, #{openid,jdbcType=VARCHAR}, #{score,jdbcType=INTEGER}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{state,jdbcType=BIT},#{maxScore,jdbcType=INTEGER}
  )
