mapper.sql:
where state = true

   and id = #{id,jdbcType=VARCHAR}


   and game_id = #{gameId,jdbcType=VARCHAR}


   and user_id = #{userId,jdbcType=VARCHAR}


   and user_account = #{userAccount,jdbcType=VARCHAR}


   and user_name = #{userName,jdbcType=VARCHAR}


   and user_image = #{userImage,jdbcType=VARCHAR}


   and score = #{score,jdbcType=INTEGER}


   and share_sn = #{shareSn,jdbcType=VARCHAR}


   and create_time = #{createTime,jdbcType=TIMESTAMP}


   and update_time = #{updateTime,jdbcType=TIMESTAMP}


   and state = #{state,jdbcType=BIT}


   and friend_score = #{friendScore,jdbcType=INTEGER}


   and friend_count = #{friendCount,jdbcType=INTEGER}

mapper.insert:
insert into t_game_record (id, game_id, user_id, 
  user_account, user_name, user_image, 
  score, share_sn, create_time, 
  update_time, state, friend_score,friend_count
  )
values (#{id,jdbcType=VARCHAR}, #{gameId,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR}, 
  #{userAccount,jdbcType=VARCHAR}, #{userName,jdbcType=VARCHAR}, #{userImage,jdbcType=VARCHAR}, 
  #{score,jdbcType=INTEGER}, #{shareSn,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{state,jdbcType=BIT}, #{friendScore,jdbcType=INTEGER}
  , #{friendCount,jdbcType=INTEGER}
  )
