mapper.sql:
where state = true

   and id = #{id,jdbcType=VARCHAR}


   and name = #{name,jdbcType=VARCHAR}


   and year = #{year,jdbcType=INTEGER}


   and start_time = #{startTime,jdbcType=TIMESTAMP}


   and end_time = #{endTime,jdbcType=TIMESTAMP}


   and create_user = #{createUser,jdbcType=VARCHAR}


   and create_time = #{createTime,jdbcType=TIMESTAMP}


   and update_user = #{updateUser,jdbcType=VARCHAR}


   and update_time = #{updateTime,jdbcType=TIMESTAMP}


   and state = #{state,jdbcType=BIT}

mapper.insert:
insert into t_holiday (id, name, start_time, 
  end_time, year,create_user, create_time, 
  update_user, update_time, state
  )
values (#{id,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{startTime,jdbcType=TIMESTAMP}, 
  #{endTime,jdbcType=TIMESTAMP},#{year,jdbcType=INTEGER}, #{createUser,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{updateUser,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}, #{state,jdbcType=BIT}
  )
