mapper.sql:
where state = true

   and id = #{id,jdbcType=VARCHAR}


   and questionnaire_id = #{questionnaireId,jdbcType=VARCHAR}


   and questionnaire_title = #{questionnaireTitle,jdbcType=VARCHAR}


   and topic_id = #{topicId,jdbcType=VARCHAR}


   and topic_name = #{topicName,jdbcType=VARCHAR}


   and user_id = #{userId,jdbcType=VARCHAR}


   and userName = #{username,jdbcType=VARCHAR}


   and user_account = #{userAccount,jdbcType=VARCHAR}


   and option_id = #{optionId,jdbcType=VARCHAR}


   and option_ordinal = #{optionOrdinal,jdbcType=VARCHAR}


   and option_name = #{optionName,jdbcType=VARCHAR}


   and create_time = #{createTime,jdbcType=TIMESTAMP}


   and update_time = #{updateTime,jdbcType=TIMESTAMP}


   and state = #{state,jdbcType=BIT}
