mapper.sql:
where state = true

   and id = #{id,jdbcType=VARCHAR}


   and name = #{name,jdbcType=VARCHAR}


   and count = #{count,jdbcType=INTEGER}


   and residue_count = #{residueCount,jdbcType=INTEGER}


   and small_image = #{smallImage,jdbcType=VARCHAR}


   and big_image = #{bigImage,jdbcType=VARCHAR}


   and intro = #{intro,jdbcType=VARCHAR}


   and create_user = #{createUser,jdbcType=VARCHAR}


   and create_time = #{createTime,jdbcType=TIMESTAMP}


   and update_user = #{updateUser,jdbcType=VARCHAR}


   and update_time = #{updateTime,jdbcType=TIMESTAMP}


   and state = #{state,jdbcType=BIT}

mapper.insert:
insert into t_award (id, name, count, 
  residue_count, small_image, big_image, 
  intro, create_user, create_time, 
  update_user, update_time, state
  )
values (#{id,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{count,jdbcType=INTEGER}, 
  #{residueCount,jdbcType=INTEGER}, #{smallImage,jdbcType=VARCHAR}, #{bigImage,jdbcType=VARCHAR}, 
  #{intro,jdbcType=VARCHAR}, #{createUser,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{updateUser,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}, #{state,jdbcType=BIT}
  )
