mapper.sql:
where state = true

   and id = #{id,jdbcType=BIGINT}


   and recharge_id = #{rechargeId,jdbcType=BIGINT}


   and status = #{status,jdbcType=INTEGER}


   and note = #{note,jdbcType=VARCHAR}


   and create_user = #{createUser,jdbcType=VARCHAR}


   and create_time = #{createTime,jdbcType=TIMESTAMP}


   and update_user = #{updateUser,jdbcType=VARCHAR}


   and update_time = #{updateTime,jdbcType=TIMESTAMP}


   and state = #{state,jdbcType=BIT}

mapper.insert:
insert into t_recharge_detail (id, recharge_id, status, 
  note, create_user, create_time, 
  update_user, update_time, state
  )
values (#{id,jdbcType=BIGINT}, #{rechargeId,jdbcType=BIGINT}, #{status,jdbcType=INTEGER}, 
  #{note,jdbcType=VARCHAR}, #{createUser,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{updateUser,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}, #{state,jdbcType=BIT}
  )

    select last_insert_id()
